let randomBanner = "";

const buttonVerProjeto = document.querySelector("#button-ver-projeto");
const buttonVerProjetoModal = document.querySelector("#button-ver-projeto-modal");
homePadrao();

buttonVerProjeto.addEventListener("click", VerProjeto);
buttonVerProjetoModal.addEventListener("click", VerProjeto);

function VerProjeto () {
    const carrosel = document.querySelectorAll(".carrosel"); 
    const button = document.querySelectorAll(".button");
    const paragrafoBanner = document.querySelector("#paragrafo-banner");
    CriarVerProjeto(projetos);

    paragrafoBanner.style.display = "none"
    carrosel.forEach((item) => {
        item.style.display = "none"; //ou block
    })
    button.forEach((item) => {
        item.style.display = "none"; //ou inline-block
    })
    buttonVerProjetoModal.addEventListener("click", desativaModal);
}


function homePadrao() {
    const conteudoProjeto = document.querySelector(".conteudo-ver-projeto");
    const body = document.querySelector("body");
    const carrosel = document.querySelectorAll(".carrosel"); 
    const button = document.querySelectorAll(".button");
    const paragrafoBanner = document.querySelector("#paragrafo-banner");

    body.addEventListener("click", (event) => {
        console.log(event)
        if(event.target.id == "button-home" || event.target.id == "logo-home"){
            conteudoProjeto.classList.remove("mostrar");
            carrosel.forEach((item) => {
                item.style.display = "block"; //block
            })
            button.forEach((item) => {
                item.style.display = "inline-block"; // inline-block
            })
            conteudoProjeto.innerHTML = "";
            paragrafoBanner.style.display = "block"
        }
    })
}


function GeraBanner(projetos) {
    const imgBanner = document.querySelector("#container-banner");
    const tituloBanner = document.querySelector("#titulo-banner");
    const paragrafoBanner = document.querySelector("#paragrafo-banner");
    randomBanner = Math.floor(Math.random() * (projetos.length - 1));

    imgBanner.style.backgroundImage = "linear-gradient(rgba(0, 0, 0, 0.85), rgba(20, 20, 20, 0.3), rgba(20, 20, 20,0.99) 100%), url(archflix-page/img/projects/" + projetos[randomBanner].banner + ")";
    tituloBanner.textContent = projetos[randomBanner].titulo;
    paragrafoBanner.textContent = projetos[randomBanner].paragrafoBanner;
    GerarModal(randomBanner, projetos);
}


function GerarModal(random, projetos) {
    const imgBannerModal = document.querySelector("#modal-banner");
    const tituloModal = document.querySelector("#titulo-modal");
    const paragrafoModal = document.querySelector("#texto-descricao");
    
    imgBannerModal.style.backgroundImage = "linear-gradient(rgba(0, 0, 0, 0.85), rgba(20, 20, 20, 0.3), rgba(20, 20, 20,0.99) 100%), url(../img/projects/" + projetos[random].banner + ")";
    tituloModal.textContent = projetos[random].titulo;
    paragrafoModal.textContent = projetos[random].textoDescricao;
}
GeraBanner(projetos);





function CriarVerProjeto(projetos) {
    const conteudoProjeto = document.querySelector(".conteudo-ver-projeto");
    conteudoProjeto.classList.add("mostrar");

    conteudoProjeto.appendChild(criarParagrafo(projetos[randomBanner].textoDescricao));
    conteudoProjeto.appendChild(criarParagrafo(projetos[randomBanner].autorTexto));

    projetos[randomBanner].imagens.forEach((imagemSrc) => {
        let imgSrc = "../img/projects/" + imagemSrc;
        let elementoImg = criarImgProjeto(imgSrc, "img-projeto");
        conteudoProjeto.appendChild(elementoImg);
    });
    
}


function criarImgProjeto(dado, classe){
    let img = document.createElement("img");
    img.classList.add(classe);
    img.setAttribute("src", dado);
    return img; 
}

function criarParagrafo(texto){
    let p = document.createElement("p");
    p.setAttribute("id", "paragrafo-ver-projeto");
    p.textContent = "Escrito por: " + texto;
    return p; 
}
